import styled from 'styled-components';

export const StyledPlayArea = styled.div`
  background: green;
  height: 100vh;
`

export const SprintDeadline = styled.div`
  width: ${1/3}vw;
  min-width: ${1/3}%;
  max-width: ${1/3}%;
  border: 1px dashed red;
  border-right: none;
  border-left: none;
  height: 30px;
  position: absolute;
  margin: 50% auto;
`;
