import React from 'react';

import {
  StyledConsoleWindow,
  CursorContainer,
  TextWindow,
  ConsoleLine,
  InputDisplay,
} from './TypingConsole.styles';
class TypingConsole extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
    }
  }

  handleChange = e => this.setState({ value: e.target.value })

  render() {
    return (
      <StyledConsoleWindow
        {...this.props}
      >
        <CursorContainer>
          >
        </CursorContainer>
        <TextWindow>
          Old Text
          Newer Text
          Text Here
          <InputDisplay>

          </InputDisplay>
          <ConsoleLine
            autoFocus
            name="password"
            value={this.state.value}
            onChange={this.handleChange}
          />
        </TextWindow>
      </StyledConsoleWindow>
    )
  }
};

export default TypingConsole;
